    preg_match('/Level (\d+)/', $a->fullname, $matchesA);
    preg_match('/Level (\d+)/', $b->fullname, $matchesB);
    
    $numA = isset($matchesA[1]) ? (int)$matchesA[1] : PHP_INT_MAX;
    $numB = isset($matchesB[1]) ? (int)$matchesB[1] : PHP_INT_MAX;

    return $numA <=> $numB;
});

